prefix : <http://example.org/>
prefix xsd: <http://www.w3.org/2001/XMLSchema#>

:Usuario (@:Hombre OR @:Mujer ) and not {:capacidad [:volar]; :estaVivo [false] }

:Animal @:Canario or @:Perro

:Hombre {
  :genero [ :Masculino ];
  :nombre xsd:string ;
  :edad xsd:integer ;
  :mascota @:Canario ;
  :mascota @:Perro *;
  :pareja @:Mujer
}

:Mujer {
  :genero [ :Femenino ];
  :nombre xsd:string ;
  :edad xsd:integer ;
  :mascota @:Canario*;
  :mascota @:Perro;
  :pareja @:Hombre
}

:Perro {
  :nombre xsd:string ;
  :edad xsd:integer ;
  :capacidad [ :ladrar ]
}

:Canario {
  :nombre xsd:string ;
  :edad xsd:integer ;
  :capacidad [ :volar ]
}
