PREFIX : <http://example.org/library#>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
PREFIX schema: <http://schema.org/>

:isMemberOf @:Library AND NOT { :isBanned [ true ] }

:Book {
  :name xsd:string ;
  :author @:Author ;
  :genre @:Genre;
  :datePublished xsd:date ;
  :isbn xsd:string ;
  :about xsd:string *
}

# Forma para un autor
:Author {
  :name xsd:string ;
  :birthDate xsd:date ;
  :nationality xsd:string ;
  :authorOf @:Book *
}

# Forma para un género
:Genre {
  :name xsd:string ;
  :genre @:SubGenre *
}

# Forma para un subgénero
:SubGenre {
  :name xsd:string
}

# Forma para la biblioteca
:Library {
  :libraryName xsd:string ;
  :hasBook @:Book * ;
  :hasBorrowingEvent @:BorrowingEvent *
}

# Forma para un evento de préstamo de libros
:BorrowingEvent {
  :borrower @:User ;
  :itemBorrowed @:Book ;
  :borrowDate xsd:date ;
  :returnDate xsd:date ;
}

:User {
  :name xsd:string ;
}
