PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX ex: <http://example.org/>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>

start =  (@<Project> OR @<Task>) AND (@<Employee> OR @<Team>)

<Project> {
  rdf:type [ ex:Project ] ;
  ex:projectID xsd:string ;
  ex:name xsd:string ;
  ex:startDate xsd:date ;
  ex:endDate xsd:date ? ;
  ex:status [ ex:NotStarted ex:InProgress ex:Completed ] ;
  ex:team @<Team> ;
  ex:tasks @<Task> *
}

<Task> {
  rdf:type [ ex:Task ] ;
  ex:taskID xsd:string ;
  ex:name xsd:string ;
  ex:description xsd:string ;
  ex:assignedTo @<Employee> ? ;
  ex:startDate xsd:date ;
  ex:endDate xsd:date ? ;
  ex:status [ ex:NotStarted ex:InProgress ex:Completed ] ;
  ex:project @<Project> ;
  ex:subTasks @<Task> *
}

<Employee> {
  rdf:type [ ex:Employee ] ;
  ex:employeeID xsd:string ;
  ex:name xsd:string ;
  ex:role [ ex:Developer ex:Manager ex:Tester ] ;
  ex:email xsd:string ;
  ex:team @<Team> ;
  ex:tasks @<Task> *
}

<Team> {
  rdf:type [ ex:Team ] ;
  ex:teamID xsd:string ;
  ex:name xsd:string ;
  ex:members @<Employee> * ;
  ex:projects @<Project> *
}
