shex:
prefix : <http://example.org/>
prefix xsd: <http://www.w3.org/2001/XMLSchema#>

:condiciones {:asistencia_minima [:80]} and {:nota_minima {:laboratorio [:5]} or {:teoria [:5]} }

:evaluacion {:nota []} and {:asistencia []}

:asignatura @:condiciones and :evaluacion

:Alumno {
  :nombre xsd:string;
  :uo xsd:integer ;
  :tiene @:asignatura *; 
  :pertenece @:grado
}

:grado {
 :nombre xsd:string;
}
